import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  Grid,
  IconButton,
  Box,
  InputBase,
  MenuItem,
  Drawer,
  List,
  ListItem,
  ListItemText,
  useTheme,
  useMediaQuery,
  Container,
} from '@mui/material';
import { Menu as MenuIcon, Search as SearchIcon } from '@mui/icons-material';
import { useLocation, useNavigate } from 'react-router-dom';

const Header = () => {
  const [activeMenuItem, setActiveMenuItem] = useState('Home'); // Track active menu
  const [drawerOpen, setDrawerOpen] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  const location = useLocation();


  const handleMenuClick = (menuItem, path) => {
    setActiveMenuItem(menuItem); // Update active menu item
    navigate(path); // Navigate to the corresponding path
    setDrawerOpen(false); // Close drawer (for mobile)
  };

  const handleDrawerToggle = () => {
    setDrawerOpen(!drawerOpen);
  };

  const menuItems = [
    { label: 'Home', path: '/' },
    { label: 'Booking', path: '/booking' },
    { label: 'Manifest', path: '/manifest' },
    { label: 'Activity', path: '/activity' },
    { label: 'Settings', path: '/settings' },
  ];

  return (
    <AppBar position="sticky">
      <Container maxWidth="xl">
        <Toolbar>
          <Grid container alignItems="center" justifyContent="space-between">
            {/* Left: Search Bar */}
            <Grid item>
              <Box
                display="flex"
                alignItems="center"
                sx={{
                  backgroundColor: 'white',
                  borderRadius: 1,
                  padding: '5px 10px',
                }}
              >
                <SearchIcon />
                <InputBase placeholder="Searchâ€¦" sx={{ marginLeft: 1 }} />
              </Box>
            </Grid>

            {/* Right: Menu Items */}
            {isMobile ? (
              <Grid item>
                <IconButton color="inherit" onClick={handleDrawerToggle}>
                  <MenuIcon />
                </IconButton>
                <Drawer anchor="right" open={drawerOpen} onClose={handleDrawerToggle}>
                  <div className="mob-header-nav-wrap">
                    <List>
                      {menuItems.map((item, index) => (
                        <ListItem
                          button
                          key={index}
                          onClick={() => handleMenuClick(item.label, item.path)}
                          selected={activeMenuItem === item.label}
                        >
                          <ListItemText primary={item.label} />
                        </ListItem>
                      ))}
                    </List>
                  </div>
                </Drawer>
              </Grid>
            ) : (
              <Grid item>
                <div className="header-nav-wrap">
                  <Box display="flex">
                    {menuItems.map((item, index) => (
                      <MenuItem
                        key={index}
                        onClick={() => handleMenuClick(item.label, item.path)}
                        sx={{
                          fontWeight: location.pathname === item.path ? 'bold' : 'normal',
                        }}
                      >
                        {item.label}
                      </MenuItem>
                    ))}
                  </Box>
                </div>
              </Grid>
            )}
          </Grid>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default Header;
